import React from 'react';

// Dummy data for articles and sidebar
const articles = [
  {
    id: 1,
    title: 'Article 1',
    author: 'John Doe',
    date: '2023-09-20',
    content: 'This is the content of article 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit...',
  },
  // Add more articles here
];

const popularArticles = [
  {
    id: 1,
    title: 'Popular Article 1',
    author: 'Jane Smith',
    date: '2023-09-18',
  },
  // Add more popular articles here
];

const Home = () => {
  return (
    <div className="home-container">
      <div className="dashboard">
        {/* Dashboard content goes here */}
        <h2>Dashboard</h2>
        {/* Add dashboard components */}
      </div>
      <div className="article-section">
        <div className="article-tabs">
          <button className="tab-button">推荐</button>
          <button className="tab-button">最新</button>
        </div>
        <div className="article-list">
          {articles.map((article) => (
            <div className="article-card" key={article.id}>
              <h3>{article.title}</h3>
              <p>
                {article.author} | {article.date}
              </p>
              <p>{article.content.slice(0, 20)}...</p>
            </div>
          ))}
        </div>
      </div>
      <div className="sidebar">
        <h2>热门文章</h2>
        <ul className="popular-articles">
          {popularArticles.map((article) => (
            <li key={article.id}>
              <a href="#">{article.title}</a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
