import React, { useState } from 'react';
import Markdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { plugins as SyntaxHighlighter } from 'prismjs';
import '../../Style/CreatorHubPage/UploadArticle.css';

export default function UploadArticle() {
  const [userInput, setUserInput] = useState('');

  // 处理用户输入变化的函数
  const handleUserInputChange = (event) => {
    setUserInput(event.target.value);
  };

  //custom render code block in markdown
  const renderCodeBlock = ({ language, value }) => {
    return (
      <SyntaxHighlighter language={language}>
        {value}
      </SyntaxHighlighter>
    );
  };

  return (
    <div className='ArticleWriteContainer'>
      <div className='UserWritingContainer'>
        <textarea
          className='userWritterBox'
          value={userInput}
          onChange={handleUserInputChange}
          placeholder="在这里输入文章内容..."
        />
      </div>
      <div className='ArticleRenderContainer'>
            <Markdown 
                className="articleContent"
                remarkPlugins={[remarkGfm]}
                components={{
                  code: renderCodeBlock, // 使用自定义的代码块渲染组件
                }}
            >
                {userInput}
            </Markdown>
      </div>
    </div>
  );
}
