import { useQuery } from "@tanstack/react-query";
import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { axObj } from "./FetchAPI";

export default function CreatorHub() {
    const param = useParams();
    const [articles , setArticles] = useState();
    const config = {
        'Authorization': `Bearer ${localStorage.getItem('token')} `
      }

//   const { isLoading, isError, data, error } = useQuery({
//     queryKey: ["creatorhub", `/articles/author/${param.id}`,config],
//     queryFn: fetchArticlesByAuthorId,
//   });
    const fetchArticlesByAuthorId = async ({queryKey}) => {
        const response = await axObj.get(`/articles/author/${param.id}`,config);
        console.log(response);
        setArticles(response);
    };

  const showToken = ()=>{
    console.log(localStorage.getItem('token'));
    fetchArticlesByAuthorId();
  }


  if(localStorage.getItem('token')){
      return (
        <div>
            <h1>==Your Articles==</h1>
            <button onClick={showToken}>show</button>
            <ul>
                {articles ? articles.map((item, index) => (
                    <li key={index}>{item.title}</li>
                )) : ''}
            </ul>
        </div>
      );
  }

  return <h2>nothing...</h2>
}
