import React from "react";
import J_logo from '../Assets/J.png'
import { NavLink, Navigate, useNavigate } from "react-router-dom";
import '../Style/NavigationBar.css';
import UserIcon from "./UserIcon";
import SearchBar from "./SearchBar";
import { useToken } from "./TokenProvider";

const NavigationBar = () => {

    const nav = useNavigate();
    const {token , setToken} = useToken();
    
    const checkLogin = (event)=>{
        if(!token){
            event.preventDefault();
            console.log("Did not login...");
            nav('/login');
        }
    }

    const logout= ()=>{
        // console.log(localStorage.getItem('token'));
        // console.log(`token hook: ${token || 'non'}`);
        localStorage.removeItem('token');
        setToken(null);
    }
    

    return (
        <div className="Container">
            <NavLink to='/home'>
                <img src={J_logo} alt="None"></img>
            </NavLink>
            <div className="searchBar">
                <SearchBar />
            </div>
            <button onClick={logout}>Log out</button>
            <ul>
                <li className="userInfo">
                    <UserIcon />
                </li>
                <li>
                    <NavLink 
                      exact
                      to="/home" 
                      style={({isActive})=> {return isActive ? {background:"#2f54eb",color:"white",borderRadius:'6px'} : null}}
                      className="nav-link">Home</NavLink>
                </li>
                <li>
                    <NavLink 
                      to="/about" 
                      style={({isActive})=> {return isActive ? {background:"#2f54eb",color:"white",borderRadius:'6px'} : null}}
                      className="nav-link">About</NavLink>
                </li>
                <li>
                    <NavLink 
                      to="/creatorhub/1001" 
                      onClick={checkLogin}
                      style={({isActive})=> {return isActive ? {background:"#2f54eb",color:"white",borderRadius:'6px'} : null}}
                      className="nav-link">Creator Hub</NavLink>
                </li>
            </ul>
        </div>
    );
}

export default NavigationBar;
