import { useQuery } from "@tanstack/react-query";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchArticlesByAuthorId } from "../FetchAPI";
import '../../Style/CreatorHubPage/CreatorHub.css';

export default function CreatorHub() {
    const param = useParams();
    const [articles , setArticles] = useState();
    const [navStat,setNavStat] = useState(1);

  const { isLoading, isError, data, error } = useQuery({
    queryKey: ["creatorhub", `/articles/author/${param.id}`],
    queryFn: fetchArticlesByAuthorId,
  });

  const showToken = ()=>{
    // console.log(localStorage.getItem('token'));
    // fetchArticlesByAuthorId();
  }

  useEffect(()=>{
    setArticles(data);
  },[data])


  if(localStorage.getItem('token')){
      return (
        <div class="creatorHubContainer">
        <nav class="navigator">
            <ul>
                <li onClick={()=>{setNavStat(1)}}>
                  投稿
                </li>
                <li onClick={()=>{setNavStat(1)}}>
                  首页
                </li>
                <li onClick={()=>{setNavStat(1)}}>
                  内容管理
                </li>
                <li onClick={()=>{setNavStat(1)}}>
                  数据中心
                </li>
                <li onClick={()=>{setNavStat(1)}}>
                  粉丝中心
                </li>
            </ul>
        </nav>
        <main class="content">
            <h1>欢迎来到创作者中心</h1>
            <p>这里是您管理内容和粉丝的地方。</p>
        </main>
    </div>
      );
  }

  return <h2>nothing...</h2>
}
