import { useLocation, useNavigate, useParams } from "react-router-dom";
import '../Style/ArticlePage.css';
import Contact from "./Contact";
import { useState , useEffect } from "react";
import axios from "axios";

//create axios object
const controller = new AbortController();

const axObj = axios.create({
    // method: 'GET',
    baseURL: 'http://localhost:8080/',
    signal: controller.signal,
});

const ArticlePage = () => {
    


    const {articleId} = useParams();//获取路径参数： /路径/参数
    
    const [data , setData] = useState();
    const [isLoading , setIsLoading] = useState(true);
    const [error , setError] = useState('');

    useEffect(()=>{
      setIsLoading(true);
  
      axObj.get(`articles/${articleId}`)
        .then(response => {
          console.log(response.data);
          setData(response.data);
          setIsLoading(false);
        })
        .catch(error => {
          setError(error);
          setIsLoading(false);
        });
    },[currentPage]);

    const article = data;
    
    return (
        <div className="article-container">
          <div className="article-header">
            <h1 className="title">{article.title}</h1>
            <div className="author-info">
              <span className="author">Author: {article.id}</span>
              <span></span>
              <span className="date">Published Date: {article.date}</span>
            </div>
          </div>
          <div className="article-content">
            {article.content}
          </div>
          <Contact />
        </div>
    );

}

export default ArticlePage;
