import React from 'react'
import axios from 'axios';


const axObj = axios.create({
    baseURL: 'http://localhost:8080',
});

export const fetchArticleById = async (_, path) => {
      const response = await axObj.get(path)
      return response.data;
};

export const fetcHArticlesByLimit = async (_, fetchArticlesByLimitPath) => {
    const response = await axios.get('http://localhost:8080'+fetchArticlesByLimitPath)
    return response.data;
};
