import { useLocation, useParams } from "react-router-dom";

const userList = [
    {
        id:1,
        username:'James'
    },
    {
        id:2,
        username:'Tom'
    },
    {
        id:3,
        username:'Romen'
    },
]

const ArticlePage = () => {
    
    const {id} = useParams();
    const URL = useLocation();

    const user = userList.find(item => item.id === +id);
    
    return(
        <div>
            <h2>{user.id}: {user.username}</h2>
        </div>
    );

}

export default ArticlePage;
