import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import Ikun from '../Assets/ikun.png';

// 定义图片放大的动画
const scaleUp = keyframes`
  0% {
    transform: scale(1);
  }
  100% {
    transform: scale(1.1);
  }
`;

// 定义下拉菜单的淡入动画
const fadeIn = keyframes`
  0% {
    opacity: 0;
    transform: translateY(-10px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

// 样式化组件
const Container = styled.div`
  position: relative;
  display: inline-block;
`;

const Image = styled.img`
  width: 200px; /* 设置图片宽度 */
  transition: transform 0.3s; /* 添加图片放大的过渡效果 */

  &:hover {
    animation: ${scaleUp} 0.3s forwards; /* 悬停时应用放大动画 */
  }
`;

const Dropdown = styled.div`
  position: absolute;
  display: inline-block;
  top: 100%; /* 下拉菜单位置在图片下方 */
  left: 0;
  visibility: hidden; /* 初始隐藏下拉菜单 */
  opacity: 0;
  transform: translateY(-10px); /* 下拉菜单向上偏移一些 */
  transition: opacity 0.3s, transform 0.3s;

  ${Container}:hover & {
    visibility: visible; /* 悬停容器时显示下拉菜单 */
    opacity: 1;
    transform: translateY(0);
    animation: ${fadeIn} 0.3s forwards; /* 淡入动画 */
  }
`;

const Dropbtn = styled.button`
  background-color: #3498db;
  color: white;
  padding: 10px;
  border: none;
  cursor: pointer;

  &:hover {
    background-color: #2980b9;
  }
`;

const DropdownContent = styled.div`
  display: none;
  position: absolute;
  background-color: #f9f9f9;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);

  ${Dropdown}:hover & {
    display: block;
  }
`;

const DropdownLink = styled.a`
  padding: 12px 16px;
  text-decoration: none;
  display: block;
  color: #333;

  &:hover {
    background-color: #ddd;
  }
`;

const MyComponent = () => {
  return (
    <Container>
      <Image src={Ikun} alt="Image" />
      <Dropdown>
        <Dropbtn>Dropdown</Dropbtn>
        <DropdownContent>
          <DropdownLink href="#">Option 1</DropdownLink>
          <DropdownLink href="#">Option 2</DropdownLink>
          <DropdownLink href="#">Option 3</DropdownLink>
        </DropdownContent>
      </Dropdown>
    </Container>
  );
};

export default MyComponent;
