import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { loginRequest } from "./FetchAPI";
import { useMutation } from "@tanstack/react-query";
import { useNavigate } from "react-router-dom";

const LoginContainer = styled.div`
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  top: 25%;
  width: 400px;
  background-color: white;
  box-shadow: 0px 0px 15px rgba(0, 0, 0, 0.1);
  padding: 20px 10px;
  box-sizing: border-box;
  border-top: 2px solid #2f54eb;
  user-select: none;
`;

const CancelButton = styled.div`
  position: absolute;
  right: 13px;
  top: 5px;
  color: rgba(0, 0, 0, 0.3);
  font-weight: bold;
  &:hover {
    color: #2f54eb;
  }
`;

const Title = styled.h2`
  margin: 0px;
  text-align: center;
  color: #2f54eb;
  margin-bottom: 5px;
`;

const Sentence = styled.span`
  display: block;
  text-align: center;
  font-size: 14px;
  color: rgba(0, 0, 0, 0.5);
  border-bottom: 1px solid rgba(0, 0, 0, 0.3);
  padding-bottom: 15px;
`;

const UserInput = styled.div`
  margin: 10px 0px;
`;

const InputField = styled.input`
  height: 50px;
  width: 80%;
  transform: translateX(10%);
  margin: 0 auto;
  border-radius: 10px;
  border: 0;
  background-color: #f5f5f5;
  margin-top: 20px;
  padding: 0px 10px;
  box-sizing: border-box;
`;

const Remind = styled.div`
  visibility: ${(props) => (props.error ? "visible" : "hidden")};
  padding-left: 40px;
  font-size: 12px;
  color: #ff4d4f;
`;

const Option = styled.div`
  span {
    display: block;
    text-align: center;
    font-size: 14px;
    color: rgba(0, 0, 0, 0.5);
    &:hover {
      color: #2f54eb;
    }
  }
`;

const SubmitButton = styled.input`
  width: 100%;
  height: 50px;
  background-color: ${(props) => (props.activated ? "#2f54eb" : "#597ef7")};
  border: 0px;
  border-radius: 10px;
  margin-top: 15px;
  font-size: 16px;
  color: white;
  font-weight: bold;
  transition: background-color 0.2s, transform 0.2s;
  &:hover {
    background-color: #1677ff;
  }
  &.activated:hover {
    background-color: #003eb3;
  }
  &.activated:active {
    transform: scale(0.98);
  }
`;

export default function Login(props) {
  const [loginState, setLoginState] = useState(false);
  const [usernameFormat, setUsernameFormat] = useState(true);
  const [passwordFormat, setPasswordFormat] = useState(true);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginForm, setLoginForm] = useState({ email: "", password: "" });
  const nav = useNavigate();

  useEffect(() => {
    setLoginForm({ email: username, password: password });
  }, [username, password]);

  const loginMutation = useMutation({
    mutationFn: (formData) => {
      return loginRequest(formData);
    },
  });

  const handleUserInfo = (e) => {
    e.preventDefault();
    console.log("userinfo");
    if (username.length <= 5) {
      setUsernameFormat(false);
    } else {
      setUsernameFormat(true);
    }
    if (password.length <= 5) {
      setPasswordFormat(false);
    } else {
      setPasswordFormat(true);
    }

    if (username.length > 5 && password.length > 5) {
      console.log("format!");
      loginMutation.mutate(loginForm);
    }
  };

  if (loginMutation.isLoading) {
    console.log("initial loading");
  }

  if (loginMutation.isSuccess && !loginState) {
    console.log("success!");
    setLoginState(true);
    console.log('My login token is : ' + loginMutation.data.data);
    localStorage.setItem('token', loginMutation.data.data);
    return nav('/home');
  }

  const closeForm = () => {
    setLoginState(true);
  };

  return (
    <LoginContainer>
      <CancelButton onClick={closeForm}>X</CancelButton>
      <Title>Welcome!</Title>
      <Sentence>"Log in with your account to start blogging!"</Sentence>
      <form onSubmit={handleUserInfo}>
        <UserInput>
          <InputField
            type="text"
            name="username"
            placeholder="Username"
            value={username}
            onChange={(e) => {
              setUsername(e.target.value);
            }}
            className={`${usernameFormat ? "" : "notFormat"}`}
          />
          <Remind error={!usernameFormat}>
            Username length must be greater than 5!
          </Remind>
          <InputField
            type="password"
            name="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className={`${passwordFormat ? "" : "notFormat"}`}
          />
          <Remind error={!passwordFormat}>
            Password length must be greater than 5!
          </Remind>
        </UserInput>
        <Option>
          <span>Forgot Password?</span>
          <span>Sign up</span>
        </Option>
        <SubmitButton
          type="submit"
          className={`${username && password ? "activated" : ""}`}
          value="Login"
          disabled={!(username && password)}
        />
      </form>
    </LoginContainer>
  );
}
