import React, { useEffect, useRef, useState } from 'react';
import Markdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { Light as SyntaxHighlighter } from 'react-syntax-highlighter';
import atomDark from 'react-syntax-highlighter/dist/esm/styles/hljs/atom-one-dark';
import '../../Style/CreatorHubPage/UploadArticle.css';
import { useParams } from 'react-router-dom';
import { useMutation } from '@tanstack/react-query';
import { createNewArticle } from '../FetchAPI';

export default function UploadArticle() {
  const [userInput, setUserInput] = useState('');
  const param = useParams();
  const [articleForm , setArticleForm] = useState({
    articleId: null,
    title: "my new article!",
    content: userInput,
    authorId: param.userId,
    publication_date: "",
    tag: "",
    views: 0,
    likes: 0,
    comments: 0
  });

  const textAreaRef = useRef(null); // 创建一个 ref 对象

  // 处理用户输入变化的函数
  const handleUserInputChange = (event) => {
    const inputValue = event.target.value;
    setUserInput(inputValue);
  };

  // ...

  useEffect(()=>{
    setArticleForm({
      articleId: null,
      title: "my new article!",
      content: userInput,
      authorId: param.userId,
      publication_date: "",
      tag: "",
      views: 0,
      likes: 0,
      comments: 0
    });
  },[userInput])

  const postArticle = () =>{
    postArticleRequest.mutate(articleForm);
  }

  const postArticleRequest = useMutation({
    mutationFn: (articleForm)=>{
      return createNewArticle(articleForm);
    }
  });

  return (
    <>
      <div className='ArticleWriteContainer'>
        <div className='UserWritingContainer'>
          <textarea
            ref={textAreaRef} // 将 ref 绑定到文本框
            className='userWritterBox'
            value={userInput}
            onChange={handleUserInputChange}
            placeholder="在这里输入文章内容..."
          />
        </div>
        <div className='ArticleRenderContainer'>
          <Markdown
            children={userInput}
            remarkPlugins={[remarkGfm]} 
            components={{
              code(props) {
                const {children, className, node, ...rest} = props
                const match = /language-(\w+)/.exec(className || '')
                return match ? (
                  <SyntaxHighlighter
                    {...rest}
                    children={String(children).replace(/\n$/, '')}
                    style={atomDark}
                    language={match[1]}
                    PreTag="div"
                  />
                ) : (
                  <code {...rest} className={className}>
                    {children}
                  </code>
                )
              },
              blockquote: (props) => {
                return (
                  <blockquote
                    style={{
                      // 添加你想要的样式属性，例如字体样式、颜色等
                      borderLeft: '2px solid #ccc',
                      margin: "0px",
                      paddingLeft: "1em"
                    }}
                  >
                    {props.children}
                  </blockquote>
                );
              },
              ul: (props) => {
                return (
                  <ul
                    style={{
                      // 添加你想要的样式属性，例如字体样式、颜色等
                      listStyleType: 'disc', // 使用实心圆点
                    }}
                  >
                    {props.children}
                  </ul>
                );
              },
              ol: (props) => {
                return (
                  <ol
                    style={{
                      // 添加你想要的样式属性，例如字体样式、颜色等
                      listStyleType: 'decimal', // 使用数字
                    }}
                  >
                    {props.children}
                  </ol>
                );
              },
            }}
          />
        </div>
      </div>
        <div className='tools' >
          <button className='postButton' onClick={postArticle}>Post</button>
        </div>
    </>
  );
}
