      setData(response.data);  
    } catch (error) {
      setError(error);
    }
    setIsLoading(false);
  },[currentPage]);

  if(isLoading){
    return (
      <h2>Loading...</h2>
    );
  }else if(error !== ''){
    return error;
  }

  if(data === undefined){
    console.log("undefined");
    return (<h2>undefined</h2>);
  }

  return (
    <>
      <div className="home-container">
        <div className="dashboard">
          <img src={welcome} alt='welcomePage'/>
        </div>
        <div className="article-section">
          <div className="article-tabs">
            <NavLink to="" 
            className="tab-button" 
            style={({isActive})=> {return isActive ? {borderBottomColor:"#2f54eb" , color:'black'} : null}}>
              Recommended
            </NavLink>
            <NavLink to="/home/recommended" 
            className="tab-button" 
            style={({isActive})=> {return isActive ? {borderBottomColor:"1px solid #2f54eb"} : null}}>
              Latest
            </NavLink>
          </div>
        
            <div className="article-list">
              {data.map((article) => (
                <div className="article-card" key={article.id}>
                  <NavLink to={`/article/${article.id}`}>
                    <h3>{article.title}</h3>
                    <p>
                      {article.authorId} | {article.publicationDate}
                    </p>
                    <p>{article.content.slice(0, 30)}...</p>
                  </NavLink>
                </div>
              ))}
            </div>
          <div className="pagination">
            {Array.from({ length: totalPages }, (_, index) => (
              <button
                key={index}
                className={currentPage === index + 1 ? 'active-page' : 'page'}
                onClick={() => handlePageChange(index + 1)}
              >
                {index + 1}
              </button>
            ))}
          </div>
        </div>
      </div>
      <TrendingSidebar articles={popularArticles} />
      < Contact />
      <SidebarBackground />
    </>
  );
};

