import { useLocation, useNavigate, useParams } from "react-router-dom";
import "../Style/ArticlePage.css";
import Contact from "./Contact";
import { useState, useEffect } from "react";
import axios from "axios";
import { useQuery } from "@tanstack/react-query";
import { fetchArticleById } from "./FetchAPI";

//create axios object
const controller = new AbortController();

const ArticlePage = () => {
  const path = window.location.pathname;
  console.log(`http://localhost:8080${path}`);
  const {isLoading , isError , data , error} = useQuery({
    queryKey: ['articles',path],
    queryFn: fetchArticleById(),
  });


  const article = data;

  if (isLoading) {
    return <h2>Is loading...</h2>;
  }

  // if (error) {
  //   return <div>Error: {error}</div>;
  // }

  // if (isError) {
  //   console.error("Error fetching article:", error);
  //   return <div>Error: Unable to fetch article.</div>;
  // }

  return (
    <div className="article-container">
      <div className="article-header">
        <h1 className="title">{article.title}</h1>
        <div className="author-info">
          <span className="author">Author: {article.authorId}</span>
          <span></span>
          <span className="date">
            Published Date: {article.publicationDate}
          </span>
        </div>
      </div>
      <div className="article-content">{article.content}</div>
      <Contact />
    </div>
  );
};

export default ArticlePage;
