import { useQuery } from "@tanstack/react-query";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchArticlesByAuthorId } from "../FetchAPI";
import '../../Style/CreatorHubPage/CreatorHub.css';
import uploadIcon from '../../Assets/upload_white.png';
import homeIcon from '../../Assets/home.png';
import articlesManageIcon from '../../Assets/articlesManage.png';
import dataViewIcon from '../../Assets/data-view.png';
import fansIcon from '../../Assets/fans.png';

export default function CreatorHub() {
    const param = useParams();
    const [articles , setArticles] = useState();
    const [navStat,setNavStat] = useState(1);

  const { isLoading, isError, data, error } = useQuery({
    queryKey: ["creatorhub", `/articles/author/${param.id}`],
    queryFn: fetchArticlesByAuthorId,
  });

  const showToken = ()=>{
    // console.log(localStorage.getItem('token'));
    // fetchArticlesByAuthorId();
  }

  useEffect(()=>{
    setArticles(data);
  },[data])


  if(localStorage.getItem('token')){
      return (
        <div class="container">
        <nav class="navigator">
            <ul>
                <li onClick={()=>{setNavStat(1)}}>
                  <img src={uploadIcon} alt="uploadIcon"/>
                  <button>投稿</button>
                </li>
                <li onClick={()=>{setNavStat(2)}}>
                  <img src={homeIcon} alt="homeIcon"/>
                  首页
                </li>
                <li onClick={()=>{setNavStat(3)}}>
                  <img src={articlesManageIcon} alt="articlesManage"/>
                  内容管理
                </li>
                <li onClick={()=>{setNavStat(4)}}>
                  <img src={dataViewIcon} alt="dataViewIcon"/>
                  数据中心
                </li>
                <li onClick={()=>{setNavStat(5)}}>
                  <img src={fansIcon} alt="fansIcon"/>
                  粉丝中心
                </li>
            </ul>
        </nav>
        <main class="content">
            <h1>欢迎来到创作者中心</h1>
            <p>这里是您管理内容和粉丝的地方。</p>
        </main>
    </div>
      );
  }

  return <h2>nothing...</h2>
}
